import React from 'react'
import './App.css';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import {Header} from './components/Header'
import {Playlists} from './components/Playlists'
import {Favorites} from './components/Favorites'
import {New} from './components/New'

function App() {
  			const makeAPICall = async () => {
					try {
						const res = await fetch('http://localhost:3000/playlists');
						const json = await res.json();
						setPlaylists(json);
					} catch (err) {
						console.error(err);
					}
				};
  	useEffect(() => {
			const makeAPICall = async () => {
				try {
					const res = await fetch('http://localhost:3000/playlists');
					const json = await res.json();
					setPlaylists(json);
				} catch (err) {
					console.error(err);
				}
			};
			makeAPICall();
		}, []);
  const emptySong = {
    title: "",
    artist: "",
    time: "",
    favorite: false,
    // playlist_id: 5
  }
  const handleFavorites = () => {

  }
  const handleCreate = (song) => {
    fetch("http://localhost:3000/playlists/5/songs", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(song),
    });
  }
  const handleDelete = (song) => {
    fetch("http://localhost:3000/playlists/5/songs/" + song.id, {
			method: 'delete',
			headers: {
				'Content-Type': 'application/json',
			},
    });
  }
  return (
		<Router>
			<div className='App'>
				<Header />
				<Playlists
					handleDelete={handleDelete}
					handleFavorites={handleFavorites}
				/>
				<Favorites
					handleDelete={handleDelete}
					handleFavorites={handleFavorites}
				/>
				<New emptySong={emptySong} handleCreate={handleCreate} />
			</div>
		</Router>
	);
}

export default App;
