import React, { useEffect, useState } from 'react';
import './App.css';
import { BrowserRouter as Router } from 'react-router-dom';
import { Header } from './components/Header';
import { Playlists } from './components/Playlists';
import { Favorites } from './components/Favorites';

function App() {
  const [playlists, setPlaylists] = useState([]);
  const [songs, setSongs] = useState([]);
	const makeAPICallSongs = async () => {
		try {
			const res = await fetch('http://localhost:3000/songs');
			const json = await res.json();
			setSongs(json);
		} catch (err) {
			console.error(err);
		}
  };
  	const makeAPICall = async () => {
		try {
			const res = await fetch('http://localhost:3000/playlists');
			const json = await res.json();
			setPlaylists(json);
		} catch (err) {
			console.error(err);
		}
	};
	useEffect(() => {
    makeAPICall();
    makeAPICallSongs();
	}, []);
	const emptySong = {
		title: '',
		artist: '',
		time: '',
		favorite: false,
  };
  const emptyPlaylist = {
    title: '',
    description: ''
  }
	const handleFavorites = (song) => {
    fetch('http://localhost:3000/playlists/5/songs/' + song.id, {
			method: 'put',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({
				favorite: !song.favorite,
			}),
		}).then(() => {
      makeAPICall();
      makeAPICallSongs();

		});
  };
	const handleCreate = (song, id) => {
		fetch('http://localhost:3000/playlists/' + id + '/songs', {
			method: 'post',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify(song),
    }).then(() => {
      makeAPICall();
    })

	};
	const handleDelete = (song) => {
		fetch('http://localhost:3000/playlists/5/songs/' + song.id, {
			method: 'delete',
			headers: {
				'Content-Type': 'application/json',
			},
		}).then(() => {
			makeAPICall();
		});
  };
  const handleCreatePlaylist = (playlist) => {
    		fetch('http://localhost:3000/playlists', {
					method: 'post',
					headers: {
						'Content-Type': 'application/json',
					},
					body: JSON.stringify(playlist),
				}).then(() => {
					makeAPICall();
				});
  }
  const handleDeletePlaylist = (playlist) => {
    		fetch('http://localhost:3000/playlists/' + playlist.id, {
					method: 'delete',
					headers: {
						'Content-Type': 'application/json',
					},
				}).then(() => {
					makeAPICall();
				});
  }
	return (
		<Router>
			<div className='App'>
				<Header />
				<Playlists emptyPlaylist={emptyPlaylist} emptySong={emptySong} handleCreate={handleCreate} handleDeletePlaylist={handleDeletePlaylist} handleCreatePlaylist={handleCreatePlaylist} playlists={playlists} setPlaylists={setPlaylists}
					handleDelete={handleDelete}
					handleFavorites={handleFavorites}
				/>
				<Favorites songs={songs}
					handleDelete={handleDelete}
					handleFavorites={handleFavorites}
				/>
			</div>
		</Router>
	);
}

export default App;
