import React, { useEffect, useState } from 'react';
import {Song} from './Song'
import {New} from './New'

export const Playlists = (props) => {
    let handleClick = props.handleClick;
    let playlists = props.playlists;
    let setPlaylists = props.setPlaylists;
    const handleCreatePlaylist = () => {
        props.handleCreatePlaylist();
    }
    const handleDeletePlaylist = (playlist) => {
        props.handleDeletePlaylist(playlist);
    }
	return (
		<div className='playlists'>
			{playlists ? (
				playlists.map(function (playlist) {
					return (
						<>
							<div className='playlist-title'>
								<h2>{playlist.title}</h2>
								<p>{playlist.description}</p>
								<button onClick={() => handleDeletePlaylist(playlist)}>
									Delete
								</button>
							</div>
							{playlist.songs ? (
								playlist.songs.map(function (song) {
									return (
										<Song
											handleDelete={props.handleDelete}
											song={song}
											handleClick={handleClick}
										/>
									);
								})
							) : (
								<p>No Songs yet</p>
							)}
						</>
					);
				})
			) : (
				<p>No Playlists</p>
			)}
			<New emptySong={props.emptySong} handleCreate={handleCreate} />
			<button onClick={handleCreatePlaylist}>Create a Playlist</button>
		</div>
	);
};
